import { useState } from 'react';
import { View, TouchableOpacity, Text, TextInput, StyleSheet } from 'react-native';

export default function App() {
  const [value, setValue] = useState("digite algo");
  return (
    <View>
      <Text>Numero1:</Text>
      <TextInput value={"digite algo"} onChangeText={(valor) => { setValue(valor); }} />
      <Text>Numero2:</Text>
      <TextInput value={"digite algo"} onChangeText={(valor2) => { setValue(valor2); }} />
      <TouchableOpacity onPress={console.log(valor+valor2)}>
        <Text>Executar</Text>
      </TouchableOpacity>
    </View>
  );
}
