import { useState } from 'react';
import { View, TouchableOpacity, Text, TextInput, StyleSheet } from 'react-native';

export default function App() {
  const [value, setValue] = useState("");
  const [value2, setValue2] = useState("");
  const [res, setRes] = useState("");
  const [result, setResult] = useState("Resultado");

  return (
    <View style={style.container}>
      <TextInput style={style.TextInput} value={value} placeholder={"Primeiro Valor"} onChangeText={(valor) => { setValue(valor); }} />
      <TextInput style={style.TextInput} value={value2} placeholder={"Primeiro Valor"} onChangeText={(valor2) => { setValue2(valor2); }} />

        <View style={style.contem}>
        <TouchableOpacity onPress={() => {
        setRes(Number(value) + Number(value2));
      }}>
        <Text style={style.Text}>+</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {
        setRes(Number(value) - Number(value2));
      }}>
        <Text style={style.Text}>-</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {
        setRes(Number(value) / Number(value2));
      }}>
        <Text style={style.Text}>/</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {
        setRes(Number(value) * Number(value2));
      }}>
        <Text style={style.Text}>*</Text>
      </TouchableOpacity>
        </View>
      <TouchableOpacity onPress={() => {
        <TextInput value={result} onChangeText={(val) => { setResult({res}); }} />
      }}>
        <Text style={style.Text2}>Calcular</Text>
        <Text style={style.Text3}>{result}</Text>
      </TouchableOpacity>

    </View >
  );
}

const style = StyleSheet.create({
  container: {
    border:'3px solid black',
    alignItems: 'center',
    width: '370px',
    height:'662px',
    margin: '2px'
},
TextInput: {
  border:'3px solid black',
  width: '300px',
  margin: '10px',
  padding: '5px'
},
Text:{
  border:'3px solid black',
  width: '50px',
  height: '40px',
  textAlign: 'center',
  margin: '10px',
  justify: "center",
  // padding: '6px',
},
Text2:{
  border:'3px solid black',
  padding: '5px',
  width: '300px',
  textAlign: 'center'
},
contem:{
  display: "flex",
  flexDirection: "row"
},
Text3:{
  padding: '5px',
  width: '200px',
  textAlign: 'center',
  padding: "60px"
}
})
