import { View, TouchableOpacity, Text, TextInput, StyleSheet } from 'react-native';

export default function App() {
  const [value, setValue] = useState("digite algo");
  const [soma, setSoma] = useState("digite algo");
  return (
    <View>
      <View>
        <Text>Numero1:</Text>
        <TextInput value={value} onChangeText={(valor) => { setValue(valor); }} />
      </View>
      <View>
        <Text>Numero2:</Text>
        <TextInput value={value} onChangeText={(valor2) => { setValue(valor2); }} />
      </View>
      <TouchableOpacity onPress={(Number(valor) + Number(valor2))}>
        <Text></Text>
        <Text>Executar</Text>
      </TouchableOpacity>
    </View>
  );
}
