import { useState } from 'react';
import { View, TouchableOpacity, Text, TextInput, StyleSheet } from 'react-native';

export default function App() {
  const [value, setValue] = useState("");
  const [value2, setValue2] = useState("");
  const [res, setRes] = useState("");

  return (
    <View style={style.container}>
      <TextInput value={value} placeholder={"Primeiro Valor"} onChangeText={(valor) => { setValue(valor); }} />
      <TextInput value={value2} placeholder={"Primeiro Valor"} onChangeText={(valor2) => { setValue2(valor2); }} />
      <TouchableOpacity onPress={() => {
        setRes(Number(value) + Number(value2));
      }}>
        <Text>+</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {
        setRes(Number(value) - Number(value2));
      }}>
        <Text>-</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {
        setRes(Number(value) / Number(value2));
      }}>
        <Text>/</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {
        setRes(Number(value) * Number(value2));
      }}>
        <Text>*</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => {

      }}>
        <Text>Calcular</Text>
        <Text>{res}</Text>
      </TouchableOpacity>

    </View >
  );
}

const style = StyleSheet.create({
  container: {
    border:'5px solid black',
    textAlign: 'center',
    alignContent: 'center'
},
TextInput: {
  border:'5px solid black',
  width: '50px'
}
})
