import { useState } from 'react';
import { View, TouchableOpacity, Text, TextInput, StyleSheet } from 'react-native';

export default function App() {
  const [ value, setValue ] = useState("Texto do State");
  return (
    <View>
      <TextInput value={value} onChangeText={(val)=>{setValue(val);}}/>
      <TextInput keyboardType='number' value={value} onChangeText={(val)=>{setValue(val);}}/>
      <TouchableOpacity onPress={ console.log(value)}>
        <Text>Executar</Text>
      </TouchableOpacity>
    </View>
  );
}
